sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var host = {};
    for (var key in source)
        if (key.startsWith("u_"))
            host[key.substring(2).toUpperCase()] = source.getValue(key);

    host["DNS_NAME"] = source.getValue("u_domain");
    host["IP_ADDRESS"] = source.getValue("u_ip");
    host["ID"] = source.getValue("u_asset_id");
	host["ALIVE"] = source.getValue("u_alive");

	host["TAGS"] = source.getValue("u_tags");
	host["TYPE"] = source.getValue("u_type");
	host["COMMENT"] = source.getValue("u_comment");

	if (host["ALIVE"] == "true")
		host["ALIVE"] = 5;
	else
		host["ALIVE"] = 6;

    var integrationRun = x_cyin_cycognito.integrationProcessGr.integration_run;
    var sourceInstance = integrationRun.implementation;

    var hostAttributeTable = integrationRun.implementation.integration.host_attributes_table + "";
    var hostInfoGR = new sn_vul.HostUtil().initializeHostAttributeRecord(host, hostAttributeTable);

    var matchResult = new sn_vul.ImportHost().hostImport(
        sourceInstance, host, "ID", integrationRun, 
        false, false, source.getValue('u_first_seen'), null, null, hostInfoGR);

    if(matchResult.insert)
      x_cyin_cycognito.cis_created++;
    else
      x_cyin_cycognito.cis_updated++;	

    ignore = true;

})(source, map, log, target);
