sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/) {

	var integrationProcess = new GlideRecord('sn_vul_integration_process');
	if (integrationProcess.get(x_cyin_cycognito.integrationProcessGr)) {
		integrationProcess.setValue('ci_created', x_cyin_cycognito.cis_created);
		integrationProcess.setValue('ci_identified', x_cyin_cycognito.cis_updated);
		integrationProcess.setValue('import_ci', x_cyin_cycognito.cis_created + x_cyin_cycognito.cis_updated);
		integrationProcess.update();
	}
})(source, map, log, target);
