sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/) {

    try {
        gs.include("CyCognitoConstants");

        var dataSourceId = import_set.data_source.sys_id + "";
        var importQueueEntryGR = new GlideRecord(QUEUE_ENTRY_TABLE);
        importQueueEntryGR.addQuery("data_source", dataSourceId);
        importQueueEntryGR.addQuery("status", "PROCESSING");
        importQueueEntryGR.orderByDesc('sys_updated_on');
        importQueueEntryGR.setLimit(1);
        importQueueEntryGR.query();
        if (importQueueEntryGR.next()) {
            x_cyin_cycognito.integrationProcessGr = importQueueEntryGR.getElement("integration_process");
            x_cyin_cycognito.domain = importQueueEntryGR.getElement("integration_process.integration_run.integration").run_as.sys_domain + '';
        }

        x_cyin_cycognito.vul_item_created = 0;
        x_cyin_cycognito.vul_item_updated = 0;
        x_cyin_cycognito.vul_item_ignored = 0;
    } catch (e) {
        gs.error("[onStart] Error while processing CyCognito Issues. Excepiton: " + e);
    }
})(source, map, log, target);
